import React, { Component } from "react";
import * as s__ from "./style";
import MenuTop from "./../MenuTop/index";
import {
  BrowserRouter,
  Switch,
  Route
} from "react-router-dom";

import FormRegister from './../FormRegister/index';
import MenuManager from './../MenuManager/index';
import InfoTable from './../InfoTable/index';
class RootContainer extends Component {

  imgArr:string[] =[];
  renderSwitch = () => {
    return  <Switch>
              <Route path="/manager/info" children={<div><InfoTable/></div>}/>
                {/* <Route exact path="/" children={<h1>Body/ Component</h1>} />
                <Route path="/about-us" children={<h1>Body/ Component about-us</h1>} />
                <Route path="/documents" children={<h1>Body/ Component documents</h1>} />
                <Route path="/git" children={<h1>Body/ Component git</h1>} /> */}
                <Route path="/register" children={<FormRegister />} />
                {/* <Route path="/sign-in" children={<h1>Body/ Component sign in</h1>} /> */}
            </Switch>
  }

  renderManager = () => {
    return <Route path="/manager" children={<MenuManager/>} />
  }

  showMSSV=(ms:string)=>{
    console.log(ms);
  }
  renderImg = () => {
    const classs = "N19DCQT";
    for(let i =1;i<10;i++){
      this.imgArr.push("http://diemtp.ptithcm.edu.vn/images/studen/"+classs+"00"+i+".jpg");
    }
    for(let i =10;i<100;i++){
      this.imgArr.push("http://diemtp.ptithcm.edu.vn/images/studen/"+classs+"0"+i+".jpg");
    }
    for(let i =100;i<200;i++){
      this.imgArr.push("http://diemtp.ptithcm.edu.vn/images/studen/"+classs+i+".jpg");
    }
  return <>{this.imgArr.map((value)=>{
    console.log(value);
    return <img src={value} style={{width:"300px"}} onClick={()=>this.showMSSV(value)}/>})}</>
  }

  render() {
    return (
      <s__.Container>
        <MenuTop />
        {/* {this.renderManager()} */}
        {this.renderSwitch()}
        {this.renderImg()}
      </s__.Container>
    );
  }
}

export default RootContainer;
