import io from "socket.io-client";
import * as config__ from "./../config";
interface config {
  URI_Scheme: string,
  host:string,
  port:string,
}
export class SocketIOClient{
  socket = io(config__.HOST);
  getDataVN = (res:any) => {
    this.socket.on('data-vn', res);
  }

  getDataGlobal = (res:any) => {
    let DATA = {};
    this.socket.on('data-global', (data:any) => {DATA = JSON.parse(data)});
    return DATA;
  }

  getDataAll = () => {
    this.socket.on('data-all', (data:any) => JSON.parse(data));
  }

  getDataLocation = () => {
    this.socket.on('data-location', (data:any) => JSON.parse(data));
  }
}
