import React,{Component} from 'react';
//style importer
import * as style__ from './style';
//component importer
import SignInForm from '../SignInForm/index';
//utils importer

type State = typeof initState;

const initState = {
  status: false as boolean
}

class UsersComponent extends Component<{},State>{

    state = initState

    formDisplay = (status:boolean) => {
        this.setState({
            status
        })
    }
    renderSignInForm = () => {
        return  <div style={{position:"absolute", top:"50%",left:"50%",transform:"translate(-50%,-50%)"}}>
                    <form action="">
                        <label htmlFor=""> CMND</label>
                        <input type="text"/>
                        <label htmlFor="">Password</label>
                        <input type="password"/>
                        <span>Bạn chưa có tài khoản ?</span>
                        <span onClick={()=>this.formDisplay(!this.state.status)}>Cancel</span>
                    </form>
                </div>
    }

    renderUsersArea = () =>{
        return  <style__.Container onClick={()=>this.formDisplay(!this.state.status)}>
                    <button>Sign in</button>
                </style__.Container>
    }

    render(){
        return (
            <React.Fragment>
                {this.renderUsersArea()}
                {this.state.status ? this.renderSignInForm():<React.Fragment />}
            </React.Fragment>
        )
    }
}

export default UsersComponent;
