import styled from "styled-components";
import AppBar from '@material-ui/core/AppBar';
import { Link } from "react-router-dom";

export const Container = styled.div`
  position:fixed;
  height: 50px;
  width: 90%;
  /* padding: 0px 20px; */
  display: flex;
  flex-direction: raw;
  justify-content: space-evenly;
  background-color: navy;
  color:white;
  align-items:stretch;
  font-size: 15px;
  font-weight: 500;
  line-height: 24px;
  /* text-shadow: 2px 0px 4px black; */
`;

export const HotLine = styled.div`
  height: 100%;
  padding: 0px;
  /* background-color: blue; */
  text-align: center;
  align-content:center;
  >p{
    position:relative;
    top:50%;
    transform:translateY(-50%);
    margin:0px;
    /* background-color: black;
    color:white; */
    align-items:stretch;
  }
`;

export const MiddleMenu = styled.div`
  height: 100%;
  /* background-color: yellow; */
`;

export const UsersArea = styled.div`
  height: 100%;
  /* background-color: red; */
`;
export const ContainerMenu = styled.div`
  display: flex;
  flex-direction: raw;
  align-items: center;
  flex-wrap: wrap;
  position:relative;
  top:50%;
  transform:translateY(-50%);
`;

export const HotLineMenu = styled.span``;

export const MiddleMenuMenu = styled.span``;

export const HeaderBtnMenu = styled.div`
  margin-left: 10px;
  margin-right: 10px;
`;
export const LinkRoute = styled(Link)`
  color: white;
  text-decoration: none;
  font-size:25px;
`;
