import React, { Component } from "react";
//style importer
import * as s__ from "./style";
import { Line  } from 'react-chartjs-2';

interface Data {
  labelName:string,
  data:number[],
}
interface DataTable{
  header:string,
  xAsis:number[],
  datas:Data[]
}
const initState = {
  dataset:{
    header:"This is header",
    xAsis:[1500, 1600, 1700, 1750, 1800, 1850, 1900, 1950, 1999, 2050],
    datas:[{
      labelName:"A",
      data:[86, 114, 106, 106, 107, 111, 133, 221, 783, 2478],
    },{
      labelName:"B",
      data:[282, 350, 411, 502, 635, 809, 947, 1402, 3700, 4267],
    },{
      labelName:"C",
      data:[168, 170, 178, 190, 203, 276, 408, 547, 675, 734],
    },{
      labelName:"D",
      data:[40, 20, 10, 16, 24, 38, 74, 167, 508, 784],
    }]
  } as DataTable
}
type State = typeof initState;

class ChartData extends Component<{},State> {

  state = initState;
  datasets:any[] = [];
  colorProps:string[] = ['#3e95cd','#8e5ea2','#3cba9f','#c45850'];

  ojbectData = (data:number[] = [],LabelName:string = "null",Color:string = "#333")=>{
    return {
      data: data,
      label: LabelName,
      borderColor: Color,
      fill: false
    }
  }

  getLineData=()=>{
    return this.state.dataset.datas.map((value:any,index:any)=>this.ojbectData(value.data,value.labelName,this.colorProps[index]))
  }

  render() {
    return(
      <s__.Container>
        <Line data={{
          labels: this.state.dataset.x,
          datasets: this.getLineData(),
        }}
        options={{
          title: {
            display: true,
            text: this.state.dataset.header,
          },
          legend: {
            display: true,
            position: "bottom"
          }
        }}
        />
      </s__.Container>
    )  
              
  }
}

export default ChartData;
