type State = typeof initState;

const initState = {
  open: true
};

class NavBar extends Component<{}, State> {
  state = initState;

  // classes = useStyles();
  handleDrawerOpen = () => {
    this.setState({
      open:true
    });
  };
  handleDrawerClose = () => {
    this.setState({
      open:false
    });
  };
  // fixedHeightPaper = clsx(this.classes.paper, this.classes.fixedHeight);

  renderNavBarDoctor = () => {
    return (
      <div>
        <ListItem button>
          <ListItemIcon>
            <DashboardIcon />
          </ListItemIcon>
          <ListItemText primary="Dashboard" />
        </ListItem>
        <ListItem button>
          <ListItemIcon>
            <ShoppingCartIcon />
          </ListItemIcon>
          <ListItemText primary="Orders" />
        </ListItem>
        <ListItem button>
          <ListItemIcon>
            <PeopleIcon />
          </ListItemIcon>
          <ListItemText primary="Customers" />
        </ListItem>
        <ListItem button>
          <ListItemIcon>
            <BarChartIcon />
          </ListItemIcon>
          <ListItemText primary="Reports" />
        </ListItem>
        <ListItem button>
          <ListItemIcon>
            <LayersIcon />
          </ListItemIcon>
          <ListItemText primary="Integrations" />
        </ListItem>
      </div>
    );
  };


  renderSeccondNavBar = () => {
    return (
      <div>
        {/* <ListSubheader inset>Saved reports</ListSubheader> */}
        <ListItem button>
          <ListItemIcon>
            <AssignmentIcon />
          </ListItemIcon>
          <ListItemText primary="Current month" />
        </ListItem>
        <ListItem button>
          <ListItemIcon>
            <AssignmentIcon />
          </ListItemIcon>
          <ListItemText primary="Last quarter" />
        </ListItem>
        <ListItem button>
          <ListItemIcon>
            <AssignmentIcon />
          </ListItemIcon>
          <ListItemText primary="Year-end sale" />
        </ListItem>
      </div>
    )
  }

  renderToolbar = () =>{
    return (
    <div 
    // className={this.classes.root}
    >
      <CssBaseline />
      <AppBar 
        position="absolute" 
        // className={clsx(this.classes.appBar, this.state.open && this.classes.appBarShift)}
      >
        <Toolbar 
          // className={this.classes.toolbar}
        >
          <IconButton
            edge="start"
            color="inherit"
            aria-label="open drawer"
            onClick={this.handleDrawerOpen}
            disabled="false"
          >
            <MenuIcon />
          </IconButton>
          <Typography component="h1" variant="h6" color="inherit" noWrap 
          // className={this.classes.title}
          >
            Dashboard
          </Typography>
          <IconButton color="inherit">
            <Badge badgeContent={4} color="secondary">
              <NotificationsIcon />
            </Badge>
          </IconButton>
        </Toolbar>
      </AppBar>
      <Drawer
        variant="permanent"
        // classes={{
        //   paper: clsx(this.classes.drawerPaper, !this.state.open && this.classes.drawerPaperClose),
        // }}
        // open={this.state.open}
      >
        <div 
        // className={this.classes.toolbarIcon}
        >
          <IconButton 
          onClick={this.handleDrawerClose}
          >
            <ChevronLeftIcon />
          </IconButton>
        </div>
        <Divider />
        <List>{this.renderNavBarDoctor()}</List>
        <Divider />
        <List>{this.renderSeccondNavBar()}</List>
      </Drawer>
    </div>
    )
  }

  render() {
    return  <s__.Container>
              {/* {this.renderNavBarDoctor()}
              {this.renderSeccondNavBar()} */}
              {this.renderToolbar()}
            </s__.Container>;
  }
}

export default NavBar;
