import React, { Component } from "react";
import { Link, Route } from "react-router-dom";
//style importer
import * as style__ from "./style";
//component importer

//import module
import APIDemo from '../../modules/api/fetchAPI';

//utils importer
//global store
//init
import {connect} from 'react-redux';
import MainState from './../../store/index';
//type
import {RegisterState} from './../../store/RegisterForm/type';
//action
import {
  setAddressRegister
  ,setDateOfBirthRegister
  ,setDisplaySignIn
  ,setEmailRegister
  ,setFullnameRegister
  ,setIdentityCardRegister
  ,setNumberRegister
  ,setPasswordRegister
} from './../../store/RegisterForm/action';
interface Props {
}



export default class RegisterForm extends Component<Props, {}> {
  module = new APIDemo();
  formDisplay = () => {
  };

  onchangeinput = () => {}

  handleRegister = () => {
    const formdata = new FormData();
    // formdata.append('identityCard','887252842');
    // formdata.append('password','123456');
    formdata.append('address','BacLieu');
    formdata.append('password','123456');
    formdata.append('phoneNumber','078441322');
    formdata.append('dateOfBirth','11/11/1999');
    formdata.append('identityCard','767172132');
    formdata.append('fullName','hsossssssnnl');
    formdata.append('email','hson83aâaas98@gmail.com');
    this.module.userRegister(
      formdata,
      (res:any)=>{
        console.log(res)},
      (err:any)=>{
        console.log(err);
      }
    );
  };

  formRegister = () => {
    return (
      <Route path="/register">
        <div
          style={{
            position: "absolute",
            top: "50%",
            left: "50%",
            transform: "translate(-50%,-50%)",
          }}
        >
          <form action="">
            <label>
              {"Họ và tên:"}
              <input 
                type="text"
                value=""
                placeholder=""
                onChange={this.onchangeinput}
              />
            </label>

            <label>
              {"CMND: "}
              <input type="text" />
            </label>

            <label>
              {"Ngày sinh: "}
              <input type="text" />
            </label>

            <label>
              {"Giới tính: "}
              <input type="text" />
            </label>

            <label>
              {"Địa chỉ: "}
              <input type="text" />
            </label>

            <label>
              {"Số điện thoại: "}
              <input type="text" />
            </label>

            <label>
              {"Email: "}
              <input type="text" />
            </label>

            <label>
              {"Password: "}
              <input type="password" />
            </label>

            <span onClick={() => this.handleRegister()}>
              Đăng ký
            </span>

            <span onClick={this.formDisplay}>Cancel</span>
          </form>
        </div>
      </Route>
    );
  };

  render() {
    return <React.Fragment>{this.formRegister()}</React.Fragment>;
  }
}
