import React, { Component } from "react";
//style importer
import * as s__ from "./style";
import {Redirect} from 'react-router-dom'
// import Link from '@material-ui/core/Link';
import { makeStyles } from '@material-ui/core/styles';
import SaveIcon from '@material-ui/icons/Save';
import CloseIcon from '@material-ui/icons/Close';
// import Button from '@material-ui/core/Button';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import ClearAllIcon from '@material-ui/icons/ClearAll';
import DeleteIcon from '@material-ui/icons/Delete';
import Checkbox from '@material-ui/core/Checkbox';

//component importer
// import MainMenu from "../MainMenu/index";
//utils importer
import RoomAPI from "../../modules/api/room";
//config importer
import * as c__ from "../../config/globtype";
import * as api__ from "../../config/apireturntype";
import { Button } from "@material-ui/core";
//global store
import { connect } from 'react-redux';
import {MainState} from '../../store/index';
//type
import {UsersState} from '../../store/User/type';


interface Props {
  UsersState: UsersState
}

type State =  typeof initState;
const initState = {
  room: [] as api__.Room[],
  patient: [] as api__.Patient[],
  shift:[] as any[],
  currentId: '',
  demo:true,
  deletemode:false,
  updatemode:false
}

class InfoTable extends Component<Props, State> {
  token = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjVlYTJhY2FlOWY4OGZiNjlmYzdmZTI0OSIsImlkUm9sZSI6eyJfaWQiOiI1ZWEyNTI5YTVmYmMxZTY0NzdjMTU4MzgiLCJuYW1lIjoiU1VQRVJfQURNSU4iLCJfX3YiOjB9LCJpc0FjdGl2ZSI6ZmFsc2UsInJvbGVOYW1lIjoiU1VQRVJfQURNSU4iLCJpYXQiOjE1ODc3MTkzNzMsImV4cCI6MTU4Nzc2MjU3M30.ETqt-RE-0CRLeFH3TbVsuJTIQ0KPgwSMUXg_I-qblNQ";
  state = initState;
  roomApi = new RoomAPI();


  //cirvle
  componentDidMount(){
    this.getAllRoom();
  }

  ////
  getIdRow=(currentId:string)=>{
    console.log(currentId);
    this.setState({
      currentId
    })
  }

  renderInputCreateRoom = () => {
    return (
      <React.Fragment>
        <s__.TextFieldArea>
            <s__.TextInput
              variant="outlined"
              label="maxNumber"
              id="maxNumber"
              type="text"
              // value={this.props.RegisterState.fullName}
              placeholder=""
              // onChange={this.onchangeFullname}
            />
          </s__.TextFieldArea>
          <s__.TextFieldArea>
            <s__.TextInput
              variant="outlined"
              label="address"
              id="address"
              type="text"
              // value={this.props.RegisterState.identityCard}
              // onChange={this.onchangeIdentityCard}
            />
          </s__.TextFieldArea>
          <s__.TextFieldArea>
            <s__.TextInput
              variant="outlined"
              label="name"
              id="name"
              type="text"
              // value={this.props.RegisterState.dateOfBirth}
              // onChange={this.onchangeDateOfBirth}
            />
          </s__.TextFieldArea>
          <s__.TextFieldArea>
            <s__.ButtonSubmit
              variant="contained"
              color="primary"
              size="large"
              startIcon={<SaveIcon />}
            >Save</s__.ButtonSubmit>
          </s__.TextFieldArea>
          <s__.TextFieldArea>
            <s__.ButtonSubmit
              variant="contained"
              color="secondary"
              size="large"
              startIcon={<CloseIcon />}
              onClick={this.cancelPopup}
            >Cancel</s__.ButtonSubmit>
          </s__.TextFieldArea>
      </React.Fragment>
    )
  }

  showPopupInsert = () => {
    return (
      <>
        <s__.PopupBackground onClick={this.cancelPopup}>
        </s__.PopupBackground>
        <s__.PopupContainer>
          <s__.PopupInfo>
            <h1>Create Room</h1>
            <h4>Input and click on submit button to create a new one, else click cancel to exist</h4>
          </s__.PopupInfo>
          <s__.PopupInput>
            {this.renderInputCreateRoom()}
          </s__.PopupInput>
        </s__.PopupContainer>
      </>
    )
  }

  cancelPopup=()=>{
    this.setState({
      updatemode:false
    })
  }

  showPopupUpdate = () => {
    this.setState({
      updatemode:true
    })
  }

  setDeleteMode = () => {
    this.setState({
      deletemode:!this.state.deletemode
    })
  }

  getAllRoom = () =>{
    // const token = this.props.UsersState.token;
    if(this.token){
      this.roomApi.getAll(this.token,(res:any)=>{
        console.log(res.data);
        this.setState({
          room:res.data.room
        })
      }, 
      (err:string)=>{
        console.log(err);
        console.log(err);
      })
    }
  }

  //API
  InsertCurrent = (formData:FormData) => {
    if(this.token){
      this.roomApi.create(this.token,formData,(res:any)=>{
        console.log(res.data);
      }, 
      (err:string)=>{
        console.log(err);
        console.log(err);
      })
    }
  }

  DeleteCurrent = (currentId:string) => {
    if(this.token){
      this.roomApi.delete(this.token,currentId,(res:any)=>{
        console.log(res.data);
      }, 
      (err:string)=>{
        console.log(err);
        console.log(err);
      })
    }
  }

  UpdateCurrent = (idRoom:string,formData:FormData) => {
    if(this.token){
      this.roomApi.upDate(this.token,idRoom,formData,(res:any)=>{
        console.log(res.data);
      }, 
      (err:string)=>{
        console.log(err);
      })
    }
  }

  GetUsers = (userId:string) => {
    if(this.token){
      this.roomApi.get(this.token,userId,(res:any)=>{
        console.log(res.data);
      },
      (err:string)=>{
        console.log(err);
        console.log(err);
      })
    }
  }

  //Render
  renderButton= () =>{
    return (
      <div style={{
        backgroundColor:"royalblue",
        height:"auto",
        borderTopRightRadius:"5px", 
        borderTopLeftRadius:"5px"
        }}
      >
        <h1 style={{marginTop:"0px"}}>Patient</h1>
        <button >Insert</button>
        <button onClick={this.setDeleteMode}>Delete</button>
        <button onClick={this.showPopupUpdate}>Update</button>
        <button >Search</button>
      </div>
      
    )
  }

  renderChildTableRoom = () => {
    return this.state.room.map((row) => (
      <s__.TableRowInfo key={row._id} onClick={()=>this.getIdRow(row._id)}>
        <TableCell>{row.name}</TableCell>
        <TableCell>{row.currentNumber}</TableCell>
        <TableCell>{row.maxNumber}</TableCell>
        <TableCell >{row.address}</TableCell>
        <TableCell style={{display:this.state.deletemode?"":"none"}} ><Checkbox/></TableCell>
      </s__.TableRowInfo>
    ))
  }

  renderGrid = () =>{
    return (
    <div style={{backgroundColor:"lightcyan",overflow:"auto",height:"auto"}}>
      <div >
        <Table size="small">
          <TableHead>
            <TableRow>
              <TableCell>Name</TableCell>
              <TableCell>Ship To</TableCell>
              <TableCell>Payment Method</TableCell>
              <TableCell>Address</TableCell>
              <TableCell style={{display:this.state.deletemode?"":"none"}}>
                <Checkbox/>Delete All</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {this.renderChildTableRoom()}
          </TableBody>
        </Table>
      </div>
    </div>
  )
  }

  renderPaper = () =>{
    return (
      <div style={{
        backgroundColor:"royalblue",
        height:"auto", 
        borderBottomRightRadius:"5px", 
        borderBottomLeftRadius:"5px"
        }}
      >
        <p style={{margin:"0px"}} >Ph√¢n trang : 1-2-3</p>
      </div>
    )
  }

  render() {
    return (
      <s__.Container style={{paddingBottom:"0px"}}>
        <div>
          {this.state.updatemode?this.showPopupInsert():<React.Fragment/>}
          {this.renderButton()}
          {this.renderGrid()}
          {this.renderPaper()}
        </div>
      </s__.Container>
    );
  }
}

const mapStateToProps = (state:MainState) =>({
  UsersState: state.UsersState
});
export default connect(mapStateToProps,{})(InfoTable);


