import axios from 'axios'

export default class RoomAPI {

    getAllRoom = (token:string,response: any, error: any) => {
      axios.get('/api/room/',{headers:{Authorization:token}})
        .then(response)
        .catch(error);
    };

    getRoom = (token:string,idRoom:string,response: any, error: any) => {
      axios.get('/api/room/'+idRoom,{headers:{Authorization:token}})
        .then(response)
        .catch(error);
    };
  
    upDateRoom = (token:string,idRoom:string,formData: FormData, response: any, error: any) => {
      const data = {
        "currentNumber":formData.get("currentNumber")
      }
      axios.put('/api/room/'+ idRoom,data,{headers:{Authorization:token}})
        .then(response)
        .catch(error);
    };

    deleteRoom = (token:string,idRoom:string, response: any, error: any) => {
        axios({
                method: 'DELETE',
                url:'/api/room/'+ idRoom,
                headers:{
                    Authorization:token
                }
            }
        )
          .then(response)
          .catch(error);
      };

    createRoom = (token:string,formData: FormData, response: any, error: any) => {
    const data = {
        "maxNumber":formData.get("maxNumber"),
        "address":formData.get("address"),
        "name":formData.get("name")
    }
    axios.post('/api/room/',data,{headers:{Authorization:token}})
        .then(response)
        .catch(error);
    };
  }
