import React, { Component } from "react";
//style importer
import * as s__ from "./style";
import {Redirect} from 'react-router-dom'
// import Link from '@material-ui/core/Link';
import { makeStyles } from '@material-ui/core/styles';
import SaveIcon from '@material-ui/icons/Save';
import CloseIcon from '@material-ui/icons/Close';
// import Button from '@material-ui/core/Button';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import ClearAllIcon from '@material-ui/icons/ClearAll';
import DeleteIcon from '@material-ui/icons/Delete';
import Checkbox from '@material-ui/core/Checkbox';

//component importer
// import MainMenu from "../MainMenu/index";
//utils importer
import RoomAPI from "../../modules/api/room";
//config importer
import * as c__ from "../../config/globtype";
import * as api__ from "../../config/apireturntype";
import { Button } from "@material-ui/core";
//global store
import { connect } from 'react-redux';
import {MainState} from '../../store/index';
//type
import {UsersState} from '../../store/User/type';


interface Props {
  UsersState: UsersState
}

type State =  typeof initState;
const initState = {
  room: [] as api__.Room[],
  patient: [] as api__.Patient[],
  shift:[] as any[],
  currentId: '',
  demo:true,
  deletemode:false,
  updatemode:false,
  numberPaper:0,
  featureType:"get" as "update" | "delete" | "insert"
}

class InfoTable extends Component<Props, State> {
  token = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjVlYTJjYjEyNzhlN2M0MjExMDc4OWQ0ZiIsImlkUm9sZSI6eyJfaWQiOiI1ZWEyY2IxMDc4ZTdjNDIxMTA3ODlkNGUiLCJuYW1lIjoiU1VQRVJfQURNSU4iLCJfX3YiOjB9LCJpc0FjdGl2ZSI6ZmFsc2UsInJvbGVOYW1lIjoiU1VQRVJfQURNSU4iLCJpYXQiOjE1ODc3NDgzNTksImV4cCI6MTU4Nzc5MTU1OX0.AcBoWMo_lOTvpWybRwykEJXoCZ5Cje0BFJT8xu4_Lbw";
  state = initState;
  roomApi = new RoomAPI();


  //cirvle
  componentDidMount(){
    this.getAllRoom();
  }

  ////
  getIdRow=(currentId:string)=>{
    console.log(currentId);
    this.setState({
      currentId
    })
  }

  renderInsertForm = () => {
    return (
      <React.Fragment>
        <s__.TextFieldArea>
            <s__.TextInput
              variant="outlined"
              label="maxNumber"
              id="maxNumber"
              type="text"
              // value={this.props.RegisterState.fullName}
              placeholder=""
              // onChange={this.onchangeFullname}
            />
          </s__.TextFieldArea>
          <s__.TextFieldArea>
            <s__.TextInput
              variant="outlined"
              label="address"
              id="address"
              type="text"
              // value={this.props.RegisterState.identityCard}
              // onChange={this.onchangeIdentityCard}
            />
          </s__.TextFieldArea>
          <s__.TextFieldArea>
            <s__.TextInput
              variant="outlined"
              label="name"
              id="name"
              type="text"
              // value={this.props.RegisterState.dateOfBirth}
              // onChange={this.onchangeDateOfBirth}
            />
          </s__.TextFieldArea>
          <s__.TextFieldArea>
            <s__.ButtonSubmit
              variant="contained"
              color="primary"
              size="large"
              startIcon={<SaveIcon />}
            >Save</s__.ButtonSubmit>
          </s__.TextFieldArea>
          <s__.TextFieldArea>
            <s__.ButtonSubmit
              variant="contained"
              color="default"
              size="large"
              startIcon={<CloseIcon />}
              onClick={this.cancelPopup}
            >Cancel</s__.ButtonSubmit>
          </s__.TextFieldArea>
      </React.Fragment>
    )
  }

  renderPopupInput = (type:string) => {
    switch(type){
      case "get":
        break;
      case "update":
        return (
          <React.Fragment>
            {this.renderUndateForm()}
          </React.Fragment>
        )
      case "delete":
        break;
      case "insert":
        return (
          <React.Fragment>
            {this.renderInsertForm()}
          </React.Fragment>
        )
    }
    
  }

  renderPopupInfo = (type:string) => {
    switch(type){
      case "get":
        break;
      case "update":
        return (
          <React.Fragment>
            <h1>Update Room</h1>
            <h4>Input and click on submit button to update number person on room, or click cancel to exist</h4>
          </React.Fragment>
        )
      case "delete":
        break;
      case "insert":
        return (
          <React.Fragment>
            <h1>Create Room</h1>
            <h4>Input and click on submit button to create a new one, else click cancel to exist</h4>
          </React.Fragment>
        )
    }
  }

  showPopupInsert = (type:string) => {
    
    return (
      <>
        <s__.PopupBackground onClick={this.cancelPopup}>
        </s__.PopupBackground>
        <s__.PopupContainer>
          <s__.PopupInfo>
            {this.renderPopupInfo(type)}
          </s__.PopupInfo>
          <s__.PopupInput>
            {this.renderPopupInput(type)}
          </s__.PopupInput>
        </s__.PopupContainer>
      </>
    )
  }

  cancelPopup=()=>{
    this.setState({
      updatemode:false
    })
  }

  showPopupUpdate = (type:"update" | "delete" | "insert") => {
    this.setState({
      updatemode:true,
      featureType:type
    })
  }

  setDeleteMode = () => {
    this.setState({
      deletemode:!this.state.deletemode
    })
  }

  getAllRoom = () =>{
    if(this.token){
      this.roomApi.getAll(this.token,(res:any)=>{
        console.log(res.data);
        this.setState({
          room:res.data.room
        })
      }, 
