import React, { Component } from "react";
//style importer
import * as s__ from "./style";

import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import DashboardIcon from '@material-ui/icons/Dashboard';
import ShoppingCartIcon from '@material-ui/icons/ShoppingCart';



//import component
import NewsCard from '../CardNews/index';
import { Route, Link } from "react-router-dom";
import { HoverMode } from 'react-particles-js';
import { List } from "@material-ui/core";

class MenuManager extends Component<{}, {}> {

  imgArr:string[] =[];
  renderPatientManager=()=>{
    return (
      <s__.ListMenu>
        <List>
        <ListItem button>
          <ListItemIcon>
            <DashboardIcon style={{color:"white"}}/>
          </ListItemIcon>
          <s__.LinkChild to="/manager/info/room"><ListItemText disableTypography={true} primary="Rooms" /></s__.LinkChild>
        </ListItem>
        <ListItem button>
          <ListItemIcon>
            <ShoppingCartIcon style={{color:"white"}}/>
          </ListItemIcon>
          <s__.LinkChild to="/manager/info/shift"><ListItemText primary="Shifts" /></s__.LinkChild>
        </ListItem>
        <ListItem button>
          <ListItemIcon>
            <ShoppingCartIcon style={{color:"white"}}/>
          </ListItemIcon>
          <s__.LinkChild to="/manager/info/patient"><ListItemText primary="Patients" /></s__.LinkChild>
        </ListItem>
        </List>
      </s__.ListMenu>
    )
  }

  showMSSV=(ms:string)=>{
    console.log(ms);
  }
  renderImg = () => {
    for(let i =1;i<10;i++){
      this.imgArr.push("http://diemtp.ptithcm.edu.vn/images/studen/N19DCMR00"+i+".jpg");
    }
  return <>{this.imgArr.map((value)=>{
    console.log(value);
    return <img src={value}  onClick={()=>this.showMSSV(value)}/>})}</>
  }

  renderBackground = () => {
    return <s__.Particless params={{
      "particles": {
          "number": {
              "value": 50
          },
          "size": {
              "value": 1.3,
              "anim": {
                "speed": 2,
                "size_min": 0.8
            }
          },
          "line_linked": {
            "enable": false,
            "blink": true,
            "width":2
          },
          "color":{
            "value":"#fff"
          }
      },
      "interactivity": {
          "events": {
              "onhover": {
                  "enable": true,
                  "mode": "connect" as HoverMode
              }
          },
          "modes": {
              "connect": {
                  "distance": 150,
                  "radius"	:	200,
                  "lineLinked":{
                    "opacity":0.2
                  }
              }
          }
      }
    }}/>
  }
  render() {
    return  (
      <s__.Container>
        {this.renderBackground()}
        {this.renderPatientManager()}
        {/* {this.renderImg()} */}
      </s__.Container>
    )
  }
}

export default MenuManager;
