import React,{Component} from 'react';
//style importer
// import * as style__ from './style';
//component importer
//utils importer
interface Props {
	isFormDisPlay: (status:boolean) => void,
	stateUser:boolean
}
type State = typeof initState;

const initState = {
  status: false as boolean
}
export default class SignInForm extends Component<Props,State>{
	state = initState

	formDisplay = () => {
        this.props.isFormDisPlay(!this.props.stateUser);
	}
	formRegisterDisplay = () => {
		this.setState({
			status:!this.state.status
		})
	}
	
	formRegister = () =>{
		this.props.isFormDisPlay(!this.props.stateUser);
		this.formRegisterDisplay();
		return <div style={{position:"absolute", top:"50%",left:"50%",transform:"translate(-50%,-50%)"}}>
					<form action="">
						<label> Họ và tên: <input type="text"/></label>
						<hr/>
						<label> CMND: <input type="text"/></label>
						<hr/>
						<label> Ngày sinh: <input type="text"/></label>
						<hr/>
						<label> Giới tính: <input type="text"/></label>
						<hr/>
						<label> Địa chỉ: <input type="text"/></label>
						<hr/>
						<label> Số điện thoại: <input type="text"/></label>
						<hr/>
						<label> Email: <input type="text"/></label>
						<hr/>
						<label>Password: <input type="password"/></label>
						<hr/>
						<button onClick={this.formDisplay}>Đăng ký</button>
						<hr/>
						<button onClick={this.formRegisterDisplay}>Cancel</button>
					</form>
				</div>
	}

    render(){
      return (
		<div style={{position:"absolute", top:"50%",left:"50%",transform:"translate(-50%,-50%)"}}>
			<form action="">
				<label> CMND: <input type="text"/></label>
				<hr/>
				<label>Password: <input type="password"/></label>
				<hr/>
				<span onClick={this.formRegister}>Bạn chưa có tài khoản ?</span>
				<hr/>
				<button onClick={this.formDisplay}>Cancel</button>
			</form>
			{this.formRegister()}
		</div>
	  )
    }
};
