import React, { Component } from "react";
//style importer
import * as s__ from "./style";

import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import DashboardIcon from '@material-ui/icons/Dashboard';
import ShoppingCartIcon from '@material-ui/icons/ShoppingCart';


import List from '@material-ui/core/List';
//import component
import NewsCard from './../NewsCard/index';
import { Route, Link } from "react-router-dom";

type State = typeof initState;

const initState = {
  open: true
};

class NavBar extends Component<{}, State> {
  state = initState;

  // classes = useStyles();

  handleDrawerOpen = () => {
    this.setState({
      open:true
    });
  };
  handleDrawerClose = () => {
    this.setState({
      open:false
    });
  };
  // fixedHeightPaper = clsx(this.classes.paper, this.classes.fixedHeight);

  renderNewsCard = () => {
    return (
      <NewsCard/>
    )
  }

  renderNavBarDoctor = () => {
    return (
      <div>
      <ListItem 
        button
        style={{width:"250px"}}
        >
        <ListItemIcon>
          <DashboardIcon />
        </ListItemIcon>
        <ListItemText primary="Get Room" />
      </ListItem>
    </div>
    );
  };

  renderSeccondNavBar = () => {
    return (
      <div>
      <ListItem button style={{width:"250px"}}>
        <ListItemIcon>
          <DashboardIcon />
        </ListItemIcon>
        <ListItemText primary="Get Shift" />
      </ListItem>
    </div>
    )
  }

  renderPatientManager=()=>{
    return (
      <>
              <ListItem button>
                <ListItemIcon>
                  <DashboardIcon />
                </ListItemIcon>
                <Link to="/manager/info/room"><ListItemText disableTypography={true} primary="Rooms" /></Link>
              </ListItem>
              <ListItem button>
                <ListItemIcon>
                  <ShoppingCartIcon />
                </ListItemIcon>
                <Link to="/manager/info/shift"><ListItemText primary="Shifts" /></Link>
              </ListItem>
              <ListItem button>
                <ListItemIcon>
                  <ShoppingCartIcon />
                </ListItemIcon>
                <Link to="/manager/info/patient"><ListItemText primary="Patients" /></Link>
              </ListItem>
            </>
    )
  }

  renderToolbar = () =>{
    return (
      <
        // variant="permanent"
        // style={{position:"relative",width:"40px"}}
        // classes={{
        //   paper: clsx(this.classes.drawerPaper, !this.state.open && this.classes.drawerPaperClose),
        // }}
        // open={true}
      >
        <List>{this.renderPatientManager()}</List>
      </>
    )
  }

  render() {
    return  (
              <s__.Container>
              {this.renderToolbar()}
              </s__.Container>
              )
  }
}

export default NavBar;
