  display: flex;
  flex-direction:row;
  justify-content: center;
  align-items: center;
  /* background-color: blue; */
  /* margin:5rem 0; */
`;

export const ListMenu = styled.div`
  /* width: 100%; */
`;

export const LinkChild = styled(Link)`
  text-decoration: none;
  color: white;
`;

export const ListItems = styled(ListItem)`
  :hover {
    /* background-color:white; */
    opacity: 1;
  }
`;
