  createData(2, '16 Mar, 2019', 'Tom Scholz', 'Boston, MA', 'MC ⠀•••• 1253', 100.81),
  createData(3, '16 Mar, 2019', 'Michael Jackson', 'Gary, IN', 'AMEX ⠀•••• 2000', 654.39),
  createData(4, '15 Mar, 2019', 'Bruce Springsteen', 'Long Branch, NJ', 'VISA ⠀•••• 5919', 212.79),
];

const token = "";

interface Props {
  UsersState: UsersState
}
type State = initState;
const initState = {
  room: [] as any

}

class TableInfo extends Component<Props, State> {

  modules = new RoomAPI();

  componentDidMount(){
    const token = this.props.UsersState.token;
    if(token){
      this.modules.getAllRoom(token,(res:any)=>{
        console.log(res.data.room);
      }, 
      (err:string)=>{
        console.log(err);
      })
    }
    console.log('dang nhap that bai');
  }

  getKey=(key:number)=>{
    console.log(key);
  }

  InsertCurrent = () => {

  }

  DeleteCurrent = () => {
    
  }

  UpdateCurrent = () => {
    
  }

  GetUsers = () => {
    
  }

  renderButton= () =>{
    return (
      <div style={{
        backgroundColor:"mediumspringgreen",
        height:"auto",
        borderTopRightRadius:"5px", 
        borderTopLeftRadius:"5px"
        }}
      >
        <h1 style={{marginTop:"0px"}}>Patient</h1>
        {/* <Button onClick={this.InsertCurrent}>Insert</Button> */}
        {/* <Button onClick={this.DeleteCurrent}>Delete</Button> */}
        <Button onClick={this.UpdateCurrent}>Update</Button>
        <Button onClick={this.GetUsers}>Search</Button>
      </div>
      
    )
  }

  renderGrid = () =>{
    return (
    <div style={{backgroundColor:"lightcyan",overflow:"auto",height:"300px"}}>
      <div >
        <Table size="small">
          <TableHead>
            <TableRow>
              <TableCell>Date</TableCell>
              <TableCell>Name</TableCell>
              <TableCell>Ship To</TableCell>
              <TableCell>Payment Method</TableCell>
              <TableCell align="right">Sale Amount</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {rows.map((row,index) => (
              <s__.TableRowInfo key={row.id} onClick={()=>this.getKey(index)}>
                <TableCell>{row.date}</TableCell>
                <TableCell>{row.name}</TableCell>
                <TableCell>{row.shipTo}</TableCell>
                <TableCell>{row.paymentMethod}</TableCell>
                <TableCell align="right">{row.amount}</TableCell>
              </s__.TableRowInfo>
            ))}
          </TableBody>
        </Table>
      </div>
    </div>
  )
  }

  renderPaper = () =>{
    return (
      <div style={{
        backgroundColor:"mediumspringgreen",
        height:"auto", 
        borderBottomRightRadius:"5px", 
        borderBottomLeftRadius:"5px"
        }}
      >
        <p style={{margin:"0px"}} >Phân trang : 1-2-3</p>
      </div>
    )
  }

  render() {
    return (
      <s__.Container style={{paddingBottom:"0px"}}>
        <div>
          {this.renderButton()}
          {this.renderGrid()}
          {this.renderPaper()}
        </div>
      </s__.Container>
    );
  }
}

const mapStateToProps = (state:MainState) =>({
  UsersState: state.UsersState
});
export default connect(mapStateToProps,{})(TableInfo);
