import axios from 'axios'

export default class RoomAPI {

    getAllRoom = (token:string,response: any, error: any) => {
      axios.get('/api/room/',{headers:{Authorization:token}})
        .then(response)
        .catch(error);
    };

    getRoom = (token:string,idRoom:string,response: any, error: any) => {
      axios.get('/api/room/'+idRoom,{headers:{Authorization:token}})
        .then(response)
        .catch(error);
    };
  
    upDateRoom = (token:string,idRoom,formData: any, response: any, error: any) => {
      const data = {
        "identityCard":formData.get("identityCard"),
        "password":formData.get("password")
      }
      axios.put('/api/room/'+ idRoom,data,{headers:{Authorization:token}})
        .then(response)
        .catch(error);
    };

    deleteRoom = (token:string, response: any, error: any) => {
        const id:string = "";
        axios({
                method: 'DELETE',
                url:'/api/room/'+ id,
                headers:{
                    Authorization:token
                }
            }
        )
        // axios.post('/api/room/'+ id,{headers:{Authorization:token}})
          .then(response)
          .catch(error);
      };

    createRoom = (token:string,formData: any, response: any, error: any) => {
    const data = {
        "identityCard":formData.get("identityCard"),
        "password":formData.get("password")
    }
    axios.post('/api/room/',data,{headers:{Authorization:token}})
        .then(response)
        .catch(error);
    };
  }
