import React, { Component } from "react";
//style importer
import * as s__ from "./style";

import Link from '@material-ui/core/Link';
import { makeStyles } from '@material-ui/core/styles';
// import Button from '@material-ui/core/Button';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
//component importer
// import MainMenu from "../MainMenu/index";
//utils importer
import RoomAPI from "../../modules/api/room";
//config importer
import * as c__ from "../../config/component";
import { Button } from "@material-ui/core";
//global store
import { connect } from 'react-redux';
import {MainState} from '../../store/index';
//type
import {UsersState} from '../../store/User/type';


interface Props {
  UsersState: UsersState
}

type State =  typeof initState;
const initState = {
  room: [] as c__.Room[],
  patient: [] as c__.Patient[],
  shift:[] as any[],
  currentId: '',
}

class InfoTable extends Component<Props, State> {

  state = initState;
  roomApi = new RoomAPI();

  componentDidMount(){
    this.getAllRoom();
  }

  getAllRoom = () =>{
    const token = this.props.UsersState.token;
    if(token){
      this.roomApi.getAll(token,(res:any)=>{
        console.log(res.data.room);
        // this.setState({
        //   room:res.data.room
        // })
      }, 
      (err:string)=>{
        console.log(err);
      })
    }
    console.log('dang nhap that bai');
  }

  getKey=(currentId:string)=>{
    this.setState({
      currentId
    })
  }

  InsertCurrent = () => {

  }

  DeleteCurrent = () => {
    
  }

  UpdateCurrent = () => {
    
  }

  GetUsers = () => {
    
  }

  renderButton= () =>{
    return (
      <div style={{
        backgroundColor:"mediumspringgreen",
        height:"auto",
        borderTopRightRadius:"5px", 
        borderTopLeftRadius:"5px"
        }}
      >
        <h1 style={{marginTop:"0px"}}>Patient</h1>
        {/* <Button onClick={this.InsertCurrent}>Insert</Button> */}
        {/* <Button onClick={this.DeleteCurrent}>Delete</Button> */}
        <Button onClick={this.UpdateCurrent}>Update</Button>
        <Button onClick={this.GetUsers}>Search</Button>
      </div>
      
    )
  }

  renderChildTableRoom = () => {
    return this.state.room.map((row) => (
      <s__.TableRowInfo key={row._id} onClick={()=>this.getKey(row._id)}>
        <TableCell>{row.name}</TableCell>
        <TableCell>{row.currentNumber}</TableCell>
        <TableCell>{row.maxNumber}</TableCell>
        <TableCell >{row.address}</TableCell>
        {/* <TableCell align="right">{row.address}</TableCell> */}
      </s__.TableRowInfo>
    ))
  }

  renderGrid = () =>{
    return (
    <div style={{backgroundColor:"lightcyan",overflow:"auto",height:"300px"}}>
      <div >
        <Table size="small">
          <TableHead>
            <TableRow>
              <TableCell>Date</TableCell>
              <TableCell>Name</TableCell>
              <TableCell>Ship To</TableCell>
              <TableCell>Payment Method</TableCell>
              <TableCell>Address</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {this.renderChildTableRoom()}
          </TableBody>
        </Table>
      </div>
    </div>
  )
  }

  renderPaper = () =>{
    return (
      <div style={{
        backgroundColor:"mediumspringgreen",
        height:"auto", 
        borderBottomRightRadius:"5px", 
        borderBottomLeftRadius:"5px"
        }}
      >
        <p style={{margin:"0px"}} >Ph√¢n trang : 1-2-3</p>
      </div>
    )
  }

  render() {
    return (
      <s__.Container style={{paddingBottom:"0px"}}>
        <div>
          {this.renderButton()}
          {this.renderGrid()}
          {this.renderPaper()}
        </div>
      </s__.Container>
    );
  }
}

const mapStateToProps = (state:MainState) =>({
  UsersState: state.UsersState
});
export default connect(mapStateToProps,{})(InfoTable);
