import React, { Component } from "react";
//style importer
import * as s__ from "./style";
import { Line, Bar, Doughnut  } from 'react-chartjs-2';

interface Data {
  labelName:string,
  data:number[],
}
interface DataTable{
  header:string,
  xAsis:number[],
  datas:Data[]
}

// interface 
const initState = {
  datasetLineChart:{
    header:"This is header",
    xAsis:[1500, 1600, 1700, 1750, 1800, 1850, 1900, 1950, 1999, 2050],
    datas:[{
      labelName:"A",
      data:[86, 114, 106, 106, 107, 111, 133, 221, 783, 2478],
    },{
      labelName:"B",
      data:[282, 350, 411, 502, 635, 809, 947, 1402, 3700, 4267],
    },{
      labelName:"C",
      data:[168, 170, 178, 190, 203, 276, 408, 547, 675, 734],
    },{
      labelName:"D",
      data:[40, 20, 10, 16, 24, 38, 74, 167, 508, 784],
    }]
  } as DataTable
}
type State = typeof initState;
interface Props{

}

class ChartData extends Component<Props,State> {

  state = initState;
  datasets:any[] = [];
  colorProps:string[] = ['#3e95cd','#8e5ea2','#3cba9f','#c45850'];

  DonutColor:string[] = ['#3e95cd','#8e5ea2','#3cba9f','#c45850'];
  DonutLabel:string[]= [
            "Africa",
            "Asia",
            "Europe",
            "Latin America"
          ]
  DonutHeader:string = "Predicted world population (millions) in 2050";

  BarColor:string[] = [
            "#3e95cd",
            "#8e5ea2",
            "#3cba9f",
            "#e8c3b9",
            "#c45850"
          ];
  BarLabel:string[]=[
              "Africa",
              "Asia",
              "Europe",
              "Latin America",
              "North America"
            ];
  BarHeader="Predicted world population (millions) in 2050";

  ojbectData = (data:number[] = [],LabelName:string = "null",Color:string = "#333")=>{
    return {
      data: data,
      label: LabelName,
      borderColor: Color,
      fill: false
    }
  }

  objectData1 = ()=>{
      return {
      label: "Population (millions)",
      backgroundColor: this.DonutColor,
      data: [2478, 5267, 734, 784]
    }
  }

  objectData2 = () => {
    return {
      label: "Population (millions)",
      backgroundColor: this.BarColor,
      data: [2478, 5267, 734, 784,432]
    }
  }

  getData=(typeChart:string)=>{
    switch(typeChart){
      case "Line":
        return this.state.datasetLineChart.datas.map((value:any,index:any)=>this.ojbectData(value.data,value.labelName,this.colorProps[index]))
      case "Doughnut":
        return [this.objectData1()];
      case "Bar":
        return [this.objectData2()];
    }
  }

  renderChart=(typeChart:string)=>{
    switch(typeChart){
      case "Line":
        return;
      case "Doughnut":
        return
    }
  }

  render() {
    return(
      <s__.Container>
        <Bar
          data={{
            labels: this.BarLabel,
            datasets: this.getData("Bar")
          }}
          options={{
            legend: { display: false },
            title: {
              display: true,
              text: this.BarHeader
            }
          }}
        />

        <Line 
        data={{
          labels: this.state.datasetLineChart.xAsis,
          datasets: this.getData("Line"),
        }}
        options={{
          title: {
            display: true,
            text: this.state.datasetLineChart.header,
          },
          legend: {
            display: true,
            position: "bottom"
          }
        }}
        />

        <Doughnut
        data={{
          labels: this.DonutLabel,
          datasets: this.getData("Doughnut")
        }}
        options={{
          title: {
            display: true,
            text: this.DonutHeader
          }
        }}
        />

      </s__.Container>
    )  
              
  }
}

export default ChartData;
