import React, { Component } from "react";
//style importer
import * as s__ from "./style";
//component importer
import SignInForm from "../SignInForm/index";
//utils importer

class UsersComponent extends Component<{},> {

  formDisplay = (status: boolean) => {
    this.setState({
      status,
    });
	};
	
  renderSignInForm = () => {
    return (
      <SignInForm
        isFormDisPlay={this.formDisplay}
        stateUser={this.state.status}
      />
    );
  };

  renderUsersArea = () => {
    return (
      <s__.Container onClick={() => this.formDisplay(!this.state.status)}>
					<s__.HeaderBtn>Sign in</s__.HeaderBtn>
      </s__.Container>
    );
  };

  render() {
    return (
      <React.Fragment>
        {this.renderUsersArea()}
        {this.state.status ? this.renderSignInForm() : <React.Fragment />}
      </React.Fragment>
    );
  }
}

export default UsersComponent;
