type State = typeof initState;

const initState = {
  open: true
};

class NavBar extends Component<{}, State> {
  state = initState;

  // classes = useStyles();

  handleDrawerOpen = () => {
    this.setState({
      open:true
    });
  };
  handleDrawerClose = () => {
    this.setState({
      open:false
    });
  };
  // fixedHeightPaper = clsx(this.classes.paper, this.classes.fixedHeight);

  renderNavBarDoctor = () => {
    return (
        <ExpansionPanel>
          <ExpansionPanelSummary
            expandIcon={<ExpandMoreIcon />}
            aria-controls="panel1ah-content"
            id="panel1ah-header"
          >
            <ListItemIcon>
            <AssignmentIcon />
            </ListItemIcon>
            Room Mamanger
          </ExpansionPanelSummary>
          <ExpansionPanelDetails>
            <div>
              <ListItem button>
                <ListItemIcon>
                  <DashboardIcon />
                </ListItemIcon>
                <ListItemText primary="Get Room" />
              </ListItem>
              <ListItem button>
                <ListItemIcon>
                  <ShoppingCartIcon />
                </ListItemIcon>
                <ListItemText primary="Update Room" />
              </ListItem>
              <ListItem button>
                <ListItemIcon>
                  <ShoppingCartIcon />
                </ListItemIcon>
                <ListItemText primary="Delete Room" />
              </ListItem>
              <ListItem button>
                <ListItemIcon>
                  <PeopleIcon />
                </ListItemIcon>
                <ListItemText primary="Get All Room" />
              </ListItem>
              <ListItem button>
                <ListItemIcon>
                  <BarChartIcon />
                </ListItemIcon>
                <ListItemText primary="Create Room" />
              </ListItem>
            </div>
          </ExpansionPanelDetails>
        </ExpansionPanel>
    );
  };

  renderSeccondNavBar = () => {
    return (
        <ExpansionPanel>
          <ExpansionPanelSummary
            expandIcon={<ExpandMoreIcon />}
            aria-controls="panel1ah-content"
            id="panel1ah-header"
          >
            <ListItemIcon>
            <AssignmentIcon />
            </ListItemIcon>
            Shift Mamanger
          </ExpansionPanelSummary>
          <ExpansionPanelDetails>
            <div>
              <ListItem button>
                <ListItemIcon>
                  <DashboardIcon />
                </ListItemIcon>
                <ListItemText primary="Get Shift" />
              </ListItem>
              <ListItem button>
                <ListItemIcon>
                  <ShoppingCartIcon />
                </ListItemIcon>
                <ListItemText primary="Update Shift" />
              </ListItem>
              <ListItem button>
                <ListItemIcon>
                  <ShoppingCartIcon />
                </ListItemIcon>
                <ListItemText primary="Delete Shift" />
              </ListItem>
              <ListItem button>
                <ListItemIcon>
                  <PeopleIcon />
                </ListItemIcon>
                <ListItemText primary="Get All Shift" />
              </ListItem>
              <ListItem button>
                <ListItemIcon>
                  <BarChartIcon />
                </ListItemIcon>
                <ListItemText primary="Create Shift" />
              </ListItem>
            </div>
          </ExpansionPanelDetails>
        </ExpansionPanel>
    )
  }

  renderPatientManager=()=>{
    return (
      <>
              <ListItem button>
                <ListItemIcon>
                  <DashboardIcon />
                </ListItemIcon>
                <ListItemText disableTypography={true} primary="Register Patient" />
              </ListItem>
              <ListItem button>
                <ListItemIcon>
                  <ShoppingCartIcon />
                </ListItemIcon>
                <ListItemText primary="Get list patient" />
              </ListItem>
              <ListItem button>
                <ListItemIcon>
                  <ShoppingCartIcon />
                </ListItemIcon>
                <ListItemText primary="Add patient to room" />
              </ListItem>
            </>
    )
  }

  renderToolbar = () =>{
    return (
      <
        // variant="permanent"
        // style={{position:"relative",width:"40px"}}
        // classes={{
        //   paper: clsx(this.classes.drawerPaper, !this.state.open && this.classes.drawerPaperClose),
        // }}
        // open={true}
      >
        <div style={{display:"block"}}
        // className={this.classes.toolbarIcon}
        >Close
          <IconButton
            onClick={this.handleDrawerClose}
          >
            <ChevronLeftIcon />
          </IconButton>
        </div>
        <Divider />
        <List>{this.renderNavBarDoctor()}</List>
        <Divider />
        <List>{this.renderSeccondNavBar()}</List>
        <Divider />
        <List>{this.renderPatientManager()}</List>
      </>
    )
  }

  render() {
    return  <div style={{
              height:"100%",
              width:"100%",
              overflow:"scroll"
            }}
            >
              <s__.Container>
              {this.renderToolbar()}
              </s__.Container>
            </div>;
  }
}

export default NavBar;
